import 'isomorphic-fetch'

export default class extends React.Component{

    static async getInitialProps(){
        let req = await fetch 
        ('https://api.audioboom.com/channels/recommended')
        let {body: channels} = await req.json()

        return { channels }
    }
    render(){
        const { channels } = this.props
        return <div>
                <header>Podcasts</header>
                <div className="channels">
                    { channels.map((channels) => (
                        <div className="channel">
                            <img src={ channels.urls.logo_image.original } alt=""></img>
                            <h2>{ channels.title }</h2>
                        </div>
                    )) }
                </div>
                <style jsx>{`
                header {
                    color: #fff;
                    background: #8756ca;
                    padding: 15px;
                    text-align: center;
                    grid-template-columns: repeat(auto-fill, minmax);
                }
                .channels {
                    display: grid;
                    grid-gap: 15px;
                    padding: 15px;
                }
                .channel {
                    display: block;
                    border-radius: 3px;
                    box-shadow: 0px 2px rgba(0,0,0,0.15);
                    margin-bottom: 0.5em;
                }

                .channel img{
                    width: 100%;
                }

                h2{
                    padding: 5px;
                    font-size: 0.9em;
                    font-weight: 600;
                    margin: 0;
                    text-align: center;
                }
                `}
                
                </style>

                <style jsx global>{`
                body {
                    margin: 0;
                    font-famiily: system-ui;
                    background: white;
                };
                `}</style>
            </div>
    }
}
